<?php	class QueryAgenda{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM agenda");			return $query;			$connection->close();		}				function update($agenda){			$connection = new Connection();			$connection->open();			$query=mysql_query("UPDATE agenda SET description='".$agenda->description."', date='".$agenda->date."', lieu='".$agenda->lieu."' WHERE id='".$agenda->id."'") ;			$connection->close();		}				function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM agenda WHERE id='".$id."'");			$connection->close();		}				function insert($agenda){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO agenda VALUE('','".$agenda->description."', '".$agenda->date."', '".$agenda->lieu."' )");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM agenda WHERE id='".$id."'");			return $query;			$connection->close();		}			}?>