<?php	class QueryNiveau{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM niveau");			return $query;			$connection->close();		}				function update($niveau){			$connection = new Connection();			$connection->open();			//, desc='".$annonce->desc."'			$query=mysql_query("UPDATE niveau SET label='".$niveau->label."' WHERE id='".$niveau->id."'");			$connection->close();		}				function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM niveau WHERE id='".$id."'");			$connection->close();		}				function insert($niveau){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO niveau VALUE('','".$niveau->label."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM niveau WHERE id='".$id."'");			return $query;			$connection->close();		}	}?>