<?php	class QueryNotification{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification");			return $query;			$connection->close();		}				function update($transaction){			$connection = new Connection();			$connection->open();			//, desc='".$annonce->desc."'			$query=mysql_query("UPDATE notification SET emetteurId='".$transaction->emetteurId."', recepteurId='".$transaction->recepteurId."',desc='".$transaction->desc."', date='".$transaction->date."', etat='".$transaction->etat."'");			$connection->close();		}				function delete($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM notification WHERE id='".$transaction->id."'");			$connection->close();		}				function insert($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO notification VALUE('','".$$transaction->etat."','".$transaction->desc."','".$$transaction->date."','".$transaction->emetteurId."','".$transaction->recepteurId."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE id='".$id."'");			return $query;			$connection->close();		}				function getByRecepteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE emetteurId='".$id."'");			return $query;			$connection->close();		}		function getByRecepteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE recepteurId='".$id."'");			return $query;			$connection->close();		}	}?>