<?php	class QueryNotification{		function __construct(){		}				//Fonction qui permet de slectionner toutes les informations de la table NOTIFICATION		function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification");			return $query;			$connection->close();		}				//Fonction qui permet de modifier une notification avec un Objet Notification en paramtres		function update($notification){			$connection = new Connection();			$connection->open();			$query=mysql_query("UPDATE notification SET emetteurId='".$notification->emetteurId."', recepteurId='".$notification->recepteurId."', date='".$notification->date."', etat='".$notification->etat."', type='".$notification->type."', annonceId='".$notification->annonceId."', transactionDirectId='".$notification->transactionDirectId."' WHERE id='".$notification->id."'");			$connection->close();		}				//Fonction qui permet de supprimer une notification avec un Objet Notification en paramtre		function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM notification WHERE id='".$id."'");			$connection->close();		}				//Fonction qui permet d'ajouter  une nouvelle notification avec un Objet Notification en paramtre		function insert($notification){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO notification VALUE('','".$notification->type."','".$notification->etat."','".$notification->desc."','".$notification->date."','".$notification->emetteurId."','".$notification->recepteurId."','".$notification->annonceId."','".$notification->transactionDirectId."')");			$connection->close();		}				//Fonction qui permet de retourner les informations d'une notification avec un ID en paramtre		function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE id='".$id."'");			$notif;			while($blop=mysql_fetch_object($query)){				$notif=$blop;			}			return $notif;			$connection->close();		}				//Fonction qui permet de retourner  les informations d'une notification avec l'ID Emetteur en paramtre		function getByEmetteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE emetteurId='".$id."'");			return $query;			$connection->close();		}		//Fonction qui permet de retourner  les informations d'une notification avec l'ID Rcepteur en paramtre		function getByRecepteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE recepteurId='".$id."'");			return $query;			$connection->close();		}				//Fonction qui permet de retourner  les informations d'une notification avec l'ID Annonce en paramtre		function getByAnnonceId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM notification WHERE annonceId='".$id."'");			return $query;			$connection->close();		}	}?>