<?php	class QueryTransaction{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transaction");			return $query;			$connection->close();		}				function update($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("UPDATE transaction SET emetteurId='".$transaction->emetteurId."', recepteurId='".$transaction->recepteurId."',prix='".$transaction->prix."', annonceId='".$transaction->annonceId."', annoncePropositionId='".$transaction->annoncePropositionId."' WHERE id='".$transaction->id."'");			$connection->close();		}				function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM transaction WHERE id='".$id."'");			$connection->close();		}				function insert($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO transaction VALUE('','".$transaction->annonceId."','".$transaction->recepteurId."','".$transaction->emetteurId."','".$transaction->prix."','".$transaction->annoncePropositionId."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transaction WHERE id='".$id."'");			$transaction;			while($blop=mysql_fetch_object($query)){				$transaction=$blop;			}			return $transaction;			$connection->close();		}				function getByEmetteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transaction WHERE emetteurId='".$id."'");			return $query;			$connection->close();		}		function getByRecepteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transaction WHERE recepteurId='".$id."'");			return $query;			$connection->close();		}				function getLastTransaction(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transaction ORDER BY id DESC LIMIT 1");			$transaction;			while($blop=mysql_fetch_object($query)){				$transaction=$blop;			}			return $transaction;			$connection->close();		}	}?>