<?php	class QueryTransactionDirect{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transactiondirect");			return $query;			$connection->close();		}				function update($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("UPDATE transactiondirect SET emetteurId='".$transaction->emetteurId."', recepteurId='".$transaction->recepteurId."',desc='".$transaction->desc."', prix='".$transaction->prix."' WHERE id='".$transaction->id."'");			$connection->close();		}				function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM transactiondirect WHERE id='".$id."'");			$connection->close();		}				function insert($transaction){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO transactiondirect VALUE('','".$transaction->desc."','".$transaction->recepteurId."','".$transaction->emetteurId."','".$transaction->prix."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transactiondirect WHERE id='".$id."'");			$transaction;			while($blop=mysql_fetch_object($query)){				$transaction=$blop;			}			return $transaction;			$connection->close();		}				function getByEmetteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transactiondirect WHERE emetteurId='".$id."'");			return $query;			$connection->close();		}		function getByRecepteurId($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transactiondirect WHERE recepteurId='".$id."'");			return $query;			$connection->close();		}				function getLastTransaction(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM transactiondirect ORDER BY id DESC LIMIT 1");			$transaction;			while($blop=mysql_fetch_object($query)){				$transaction=$blop;			}			return $transaction;			$connection->close();		}	}?>