<?php	class QueryTypeAnnonce{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM typeannonce");			return $query;			$connection->close();		}				function update($typeAnnonce){			$connection = new Connection();			$connection->open();			//, desc='".$annonce->desc."'			$query=mysql_query("UPDATE typeannonce SET label='".$typeAnnonce->label."' WHERE id='".$typeAnnonce->id."'");			$connection->close();		}				function delete($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM typeannonce WHERE id='".$id."'");			$connection->close();		}				function insert($typeAnnonce){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO typeannonce VALUE('','".$typeAnnonce->label."','".$typeAnnonce->type."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM typeannonce WHERE id='".$id."'");			return $query;			$connection->close();		}	}?>