<?php	class QueryUtilisateur{		function __construct(){		}				function selectAll(){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM utilisateur");			return $query;			$connection->close();		}				function update($utilisateur){			$connection = new Connection();			$connection->open();			//, desc='".$utilisateur->desc."'			$query=mysql_query("UPDATE utilisateur SET nom='".$utilisateur->nom."',prenom='".$utilisateur->prenom."', pseudo='".$utilisateur->pseudo."', password='".$utilisateur->password."', email='".$utilisateur->email."', adresse='".$utilisateur->adresse."', cp='".$utilisateur->cp."', ville='".$utilisateur->ville."', telephoneFixe='".$utilisateur->telephoneFixe."', telephonePortable='".$utilisateur->telephonePortable."',photo='".$utilisateur->photo."', poivre='".$utilisateur->poivre."', dateDerniereConnection='".$utilisateur->dateDerniereConnection."', niveauId='".$utilisateur->niveauId."' WHERE id='".$utilisateur->id."'");			$connection->close();		}				function delete($utilisateur){			$connection = new Connection();			$connection->open();			$query=mysql_query("DELETE FROM utilisateur WHERE id='".$utilisateur->id."'");			$connection->close();		}				function insert($utilisateur){			$connection = new Connection();			$connection->open();			$query=mysql_query("INSERT INTO utilisateur VALUE('','".$utilisateur->nom."','".$utilisateur->prenom."','".$utilisateur->pseudo."','".$utilisateur->password."','".$utilisateur->email."','".$utilisateur->adresse."','".$utilisateur->cp."','".$utilisateur->ville."','".$utilisateur->telephoneFixe."','".$utilisateur->telephonePortable."','".$utilisateur->photo."','".$utilisateur->poivre."','".$utilisateur->dateDerniereConnection."','".$utilisateur->niveauId."')");			$connection->close();		}				function getById($id){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM utilisateur WHERE id='".$id."'");			$user;			while($blop=mysql_fetch_object($query)){				$user=$blop;			}			return $user;			$connection->close();		}				function getByPseudoAndPassword($pseudo,$password){			$connection = new Connection();			$connection->open();			$query=mysql_query("SELECT * FROM utilisateur WHERE pseudo='".$pseudo."' AND password='".$password."'");			return $query;			$connection->close();		}	}?>